<script setup lang='ts'>
interface Props {
  value: string
}

const props = withDefaults(defineProps<Props>(), {})
const emit = defineEmits(['click'])

const onSelect = inject<(v: string) => void>('onSelect', () => {})
const sValue = inject<ComputedRef<string>>('sValue')
const shape = inject('shape')
const disabled = inject('disabled')

const isChecked = computed(() => props.value === sValue?.value)

const { bool, setTrue, setFalse } = useBoolean(false)
const outerRef = ref()
onClickOutside(outerRef, () => {
  setFalse()
})

function onCheck() {
  if (disabled)
    return

  setTrue()
  emit('click', props.value)
  onSelect(props.value)
}
</script>

<template>
  <div ref="outerRef" class="base-radio" :class="{ disabled, active: isChecked, focus: bool }" @click="onCheck">
    <span class="icon" :class="[shape]">
      <span v-if="shape === 'circle'" class="dot" />
      <span v-else class="hook" />
    </span>
    <slot />
  </div>
</template>

<style lang='scss' scoped>
.base-radio {
  color: var(--tg-text-lightgrey);
  font-size: var(--tg-font-size-default);
  cursor: pointer;
  display: flex;
  align-items: center;

  .circle {
    border-radius: 50%;

    .dot {
      border-radius: 50%;
      width: var(--tg-spacing-10);
      height: var(--tg-spacing-10);
      background-color: var(--tg-text-white);
      display: none;
    }
  }

  .square {
    border-radius: var(--tg-radius-default);

    .hook {
      width: var(--tg-spacing-21);
      height: var(--tg-spacing-21);
      background-image: url('data:image/svg+xml;base64,');
      background-size: 75%;
      background-repeat: no-repeat;
      background-position: center center;
      display: none;
    }
  }

  .icon {
    width: var(--tg-spacing-21);
    height: var(--tg-spacing-21);
    transition: all ease .25s;
    box-shadow: var(--tg-box-shadow);
    cursor: pointer;
    margin-right: var(--tg-spacing-8);
    border-width: var(--tg-border-width-sm);
    border-style: solid;
    border-color: var(--tg-secondary-main);
    display: flex;
    align-items: center;
    justify-content: center;
  }

  &:hover {
    .icon {
      border-color: var(--tg-text-grey);
    }
  }
}

.active {
  .icon {
    border-color: var(--tg-secondary-main);
    background-color: var(--tg-secondary-main);
    .dot {
      display: inline-block;
    }

    .hook {
      display: block;
    }
  }

}
.active.focus{
  .icon{
    border-color: var(--tg-text-grey);
    background-color: var(--tg-secondary-main);
  }
}

.disabled {
  cursor: not-allowed;
  opacity: 0.5;

  &:hover {
    .icon {
      cursor: not-allowed;
      border-color: var(--tg-text-grey);
    }
  }
}
</style>
